export const SEARCH_TODO = 'SEARCH_TODO';

export const searchTodo = (todoLIst, searchingWord) => {
    const newTodoList = todoLIst.filter(item => {
        const result = item.description.toLowerCase().split(' ').filter(item => item === searchingWord);
        if (Object.is(item.title.toLowerCase(), searchingWord)) {
            result.push(item);
        }
        return result;
    });
    return {
        type: SEARCH_TODO,
        payload: newTodoList
    }
};
