import { ADD_TODO } from '../action/addTodo';
import { DELETE_TODO } from '../action/deleteTodo';
import { CHANGE_DESCRIPTION } from '../action/changeDescription';
import { CHANGE_TITLE } from '../action/changeTitle';
import { SEARCH_TODO } from '../action/searchTodo';
import { load } from 'redux-localstorage-simple';

import { fromJS } from 'immutable';

let TASKS = load({ namespace: 'todo-list' });

// if (!TASKS || !TASKS.todoItems.todoList.length) {
// 	TASKS = {
// 		todoItems: {
// 			title: '',
// 			description: '',
// 			todoList: [],
// 			searchingTodo: [],
// 		},
// 	};
// }

if (!TASKS || !TASKS.todoItems.todoList.length) {
	TASKS = fromJS({
		todoItems: {
			title: '',
			description: '',
			todoList: [],
			searchingTodo: [],
		},
	});
}

const todoItems = (state = TASKS.todoItems, action) => {
	switch (action.type) {
		case CHANGE_TITLE:
			return state.todoItems.set('title', action.payload);
			// return { ...state, title: action.payload };
		case CHANGE_DESCRIPTION:
			return state.todoItems.set('description', action.payload)
			// return { ...state, description: action.payload };
		case ADD_TODO:
			return {
				...state, todoList: [...state.todoList, {
					title: state.title,
					description: state.description,
					id: action.payload.id,
					completed: false,
				}],
				title: '',
				description: '',
			};
		case DELETE_TODO:
			return { ...state, todoList: [...action.payload] };
		case SEARCH_TODO:
			return { ...state, searchingTodo: [...action.payload] };
		default:
			return { ...state };
	}
};

export default todoItems;
