const initialState = {
    title: '',
    description: '',
    todoList: [
        // {
        //     title: 'Определить состояние и сделать его наблюдаемым',
        //     description: 'Стор представляет собой ES6 class с единственным свойством show. Мы повесили на него декоратор @observable, тем самым сказали mobx-у наблюдать за ним. Show — это состояние нашей панели, которое мы будем менять.',
        //     id: '123',
        //     completed: false
        // },
        // {
        //     title: 'Создать представление, реагирующее на изменение состояния',
        //     description: 'Хорошо, что у нас уже оно есть, это component/menu/index.js. Теперь, когда состояние будет изменяться, наше меню будет автоматически перересовываться, при этом mobx найдет кротчайший путь для обновления представления. Что бы это произошло, нужно обернуть функцию, описывающую react компонент, в observer. ',
        //     id: '456',
        //     completed: false
        // }
    ]
};

const todoItems = (state = initialState, action) => {
    switch (action.type) {
        case CHANGE_TITLE:
            return {...state, title: action.payload};
        case CHANGE_DESCRIPTION:
            return {...state, description: action.payload};
        case ADD_TODO:
            return {
                ...state, todoList: [...state.todoList, {
                    title: state.title,
                    description: state.description,
                    id: UUID(),
                    completed: false
                }],
                title: '',
                description: ''
            };
        case DELETE_TODO:
            return {...state};
        default:
            return {...state};
    }
};

export default todoItems;
