import {ADD_TODO} from '../action/addTodo';
import {DELETE_TODO} from '../action/deleteTodo';

// const initialState = {
//     title: '',
//     description: '',
//     todoItems: [],
//     id: '',
//     completed: false
// };

const todo = (state = [], {type, id, title, description, completed}) => {
    switch (type) {
        case ADD_TODO:
            return [
                ...state, {
                    id,
                    title,
                    description,
                    completed
                }
        ];
        case DELETE_TODO:
            return {...state};
        default:
            return {...state};
    }
};

export default todo;
