export const SEARCH_TODO = 'SEARCH_TODO';

export const searchTodo = (todoLIst, searchingWord) => {
    const newTodoList = todoLIst.filter(item => {
        const descriptionWords = item.description.toLowerCase.split(' ').filter(item => item === searchingWord);
        console.log(descriptionWords);
        const result = [];
        if (Object.is(item.title, searchingWord) || Object.is(descriptionWords, searchingWord)) {
            result.push();
        }
        return result;
    });
    return {
        type: SEARCH_TODO,
        payload: newTodoList
    }
};
