import {ADD_TODO} from '../action/addTodo';
import {DELETE_TODO} from '../action/deleteTodo';
import {CHANGE_DESCRIPTION} from '../action/changeDescription';
import {CHANGE_TITLE} from '../action/changeTitle';

// const initialState = [
//     {
//         title: 'first',
//         description: 'first descr',
//         id: '123',
//         completed: false
//     },
//     {
//         title: 'second',
//         description: 'second descr',
//         id: '456',
//         completed: false
//     }
// ];

const initialState = {
    title: '',
    description: ''
};

const todoItems = (state = initialState, action) => {
    switch (action.type) {
        case CHANGE_TITLE:
            return {...state, title: action.payload};
        case CHANGE_DESCRIPTION:
            return {...state, description: action.payload};
        case ADD_TODO:
            return {...state, ...action.payload};
        case DELETE_TODO:
            return {...state};
        default:
            return {...state};
    }
};

export default todoItems;
