import {continueStatement} from "@babel/types";

export const SEARCH_TODO = 'SEARCH_TODO';

export const searchTodo = (todoLIst, searchingWord) => {
    const newTodoList = todoLIst.filter(item => {
        let result = [];
        const desc = item.description.toLowerCase().split(' ').filter(item => item === searchingWord);
        if (Object.is(item.title.toLowerCase(), searchingWord)) {
            result.push(item);
        } else if (desc) {
            const i = desc.filter(item => Object.is(item, searchingWord));
            result.push();
        }
        return result;
    });
    return {
        type: SEARCH_TODO,
        payload: newTodoList
    }
};
