export const SEARCH_TODO = 'SEARCH_TODO';

export const searchTodo = (todoLIst, searchingWord) => {
    const newTodoList = todoLIst.filter(item => {
        return (Object.is(item.title, searchingWord) || (item.description.toLowerCase().split(' ').filter(item => item === searchingWord)));
    });
    return {
        type: SEARCH_TODO,
        payload: newTodoList
    }
};
