import {ADD_TODO} from '../action/addTodo';
import {DELETE_TODO} from '../action/deleteTodo';

const initialState = [
    {
        title: 'first',
        description: 'first descr',
        id: '123',
        completed: false
    },
    {
        title: 'second',
        description: 'second descr',
        id: '456',
        completed: false
    }
];

const todo = (state = initialState, {type, id, title, description, completed}) => {
    switch (type) {
        case ADD_TODO:
            return [
                ...state, {
                    id,
                    title,
                    description,
                    completed
                }
            ];
        case DELETE_TODO:
            return {...state};
        default:
            return {...state};
    }
};

export default todo;
