export const ADD_TODO = 'ADD_TODO';

export const addTodo = (e, newTitle, newDescription) => {
    e.preventDefault();
    // console.log(newTitle, newDescription);
    return {
        type: ADD_TODO,
        payload: {title: newTitle, description: newDescription}
    }
};
