export const SEARCH_TODO = 'SEARCH_TODO';

export const searchTodo = (todoLIst, searchingWord) => {
    const newTodoList = todoLIst.filter(item => {
        const descriptionWords = item.description.split(' ').filter(item => item === searchingWord);
        return Object.is(item.title, searchingWord) || Object.is(descriptionWords, searchingWord);
    });
    return {
        type: SEARCH_TODO,
        payload: newTodoList
    }
};
