import {ADD_TODO} from '../action/addTodo';
import {DELETE_TODO} from '../action/deleteTodo';

const initialState = {
    title: '',
    description: '',
    todoItems: [],
    id: '',
    completed: false
};

const todoReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_TODO:
            return {...state, ...action.payload, title: action.payload.title, description: action.payload.description};
        case DELETE_TODO:
            return {...state};
        default:
            return {...state};
    }
};

export default todoReducer;
