    //         id: ''
    //     });
    // };

    // handleChange = ({target}) => {
    //     this.setState(() => ({
    //         [target.name]: target.value,
    //         id: UUID()
    //     }))
    // };

    // handleSubmit = (e) => {
    //     e.preventDefault();
    //     this.setState(({id, title, description, completed, todoItems})=> ({
    //         todoItems: [...todoItems,{id, title, description, completed}]
    //     }));
    //
    //     this.props.addTodo(this.state.todoItems);
    //     this.clearForm();
    // };

    render() {
        const {addTodo, title, description, changeTitle, changeDescription} = this.props;
        console.log(this.props);

        return (
            <div className='Form'>
                <form
                    onSubmit={(e) => addTodo(e, title, description)}
                >
                    <div className='Form-group'>
                        <label
                            className='Form-label'
                            htmlFor='todoTitle'
                        >Title:</label>
                        <input
                            className='Form-input'
                            type='text'
                            name='title'
                            placeholder='Title'
                            id='todoTitle'
                            value={title}
                            onChange={(e) => changeTitle(e.target.value)}
                        />
                    </div>
                    <div className='Form-group'>
                        <label
                            className='Form-label'
                            htmlFor='todoDescription'
                        >Description:</label>
                        <textarea
                            className='Form-input'
                            name='description'
                            id='todoDescription'
                            cols='30'
                            rows='10'
                            value={description}
                            onChange={(e) => changeDescription(e.target.value)}
                        />
                    </div>
                    <ButtonForm type='submit' name='ADD TODO'/>
                </form>
            </div>
        )
    }
}

const mapStateToProps = (store) => {
    return {
        title: store.title,
        description: store.description
    }
};

const mapDispatchToProps = {
    addTodo,
    changeDescription,
    changeTitle
};

// export default Form;
export default connect(mapStateToProps, mapDispatchToProps)(Form);
