import React, {Component} from 'react';
import {PropTypes} from 'prop-types';
import {connect} from "react-redux";
import {deleteTodo} from '../action/deleteTodo';

import TodoListItem from './TodoListItem';
import '../style/TodoList.style.scss';

class TodoList extends Component {

    render() {

        const {todoList, deleteTodo} = this.props;
        // const {searchingTodo} = this.props.todoList;
        // console.log(searchingTodo);
        console.log(this.props);
        console.log(todoList);

        const itemsTodoList = todoList.map(item =>
            <TodoListItem
                key={item.id}
                {...item}
                removeTodo={() => deleteTodo(todoList, item.id)}
            />);

        // const searchingTodoItems = todoList.searchingTodo.map(item =>
        //     <TodoListItem
        //         key={item.id}
        //         {...item}
        //         removeTodo={() => deleteTodo(todoList, item.id)}
        //     />);

        return (
            <ul className='TodoList'>
                {/*{searchingTodoItems || itemsTodoList}*/}
                {itemsTodoList}
            </ul>
        )
    }
}

TodoList.propTypes = {
    todoList: PropTypes.shape({
        title: PropTypes.string.isRequired,
        description: PropTypes.string.isRequired,
    }).isRequired,
    deleteTodo: PropTypes.func.isRequired
};

const mapStateToProps = (store) => {
    return {
        todoList: store.todoItems.todoList,
        searchingTodo: store.todoItems.searchingTodo
    }
};

const mapDispatchToProps = {
    deleteTodo
};

export default connect(mapStateToProps, mapDispatchToProps)(TodoList);
