import {ADD_TODO} from '../action/addTodo';
import {DELETE_TODO} from '../action/deleteTodo';

// const initialState = {
//     title: '',
//     description: '',
//     todoItems: [],
//     id: '',
//     completed: false
// };

const todoReducer = (state = [], action) => {
    switch (action.type) {
        case ADD_TODO:
            return [
                ...state, {
                    id:action.id,
                    title: action.title,
                    description: action.description,
                    completed: action.completed
                }
        ];
        case DELETE_TODO:
            return {...state};
        default:
            return {...state};
    }
};

export default todoReducer;
