import React, { useState } from 'react';
import { PropTypes } from 'prop-types';
import { connect } from 'react-redux';
import { searchTodo } from '../action/searchTodo';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import Badge from '@material-ui/core/Badge';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import { withStyles } from '@material-ui/core/styles';
import SearchIcon from '@material-ui/icons/Search';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MailIcon from '@material-ui/icons/Mail';
import NotificationsIcon from '@material-ui/icons/Notifications';
import MoreIcon from '@material-ui/icons/MoreVert';
import { styles } from '../style/Header.style';

const PrimarySearchAppBar = ({ classes, todoList, searchTodo }) => {
	const [anchorEl, setAnchorEL] = useState(null);
	const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState('');
	const [ring] = useState('');
	const isMenuOpen = Boolean(anchorEl);
	const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);

	const handleProfileMenuOpen = event => {
		setAnchorEL({ anchorEl: event.currentTarget });
		console.log('handleProfileMenuOpen');
	};

	const handleMenuClose = () => {
		console.log('handleMenuClose');
		setAnchorEL({ anchorEl: null });
		handleMobileMenuClose();
	};

	const handleMobileMenuOpen = event => {
		setMobileMoreAnchorEl({ mobileMoreAnchorEl: event.currentTarget });
		console.log('handleMobileMenuOpen');
	};

	const handleMobileMenuClose = () => {
		setMobileMoreAnchorEl({ mobileMoreAnchorEl: null });
		console.log('handleMobileMenuClose');
	};


	const renderMenu = (
		<Menu
			anchorEl={anchorEl}
			anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
			transformOrigin={{ vertical: 'top', horizontal: 'right' }}
			open={isMenuOpen}
			onClose={() => handleMenuClose()}
		>
			<MenuItem onClick={() => handleMenuClose()}>Profile</MenuItem>
			<MenuItem onClick={() => handleMenuClose()}>My account</MenuItem>
		</Menu>
	);

	const renderMobileMenu = (
		<Menu
			anchorEl={mobileMoreAnchorEl}
			anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
			transformOrigin={{ vertical: 'top', horizontal: 'right' }}
			open={isMobileMenuOpen}
			onClose={() => handleMenuClose()}
		>
			<MenuItem onClick={() => handleMobileMenuClose()}>
				<IconButton color="inherit">
					<Badge badgeContent={todoList.length} color="secondary">
						<MailIcon/>
					</Badge>
				</IconButton>
				<p>Messages</p>
			</MenuItem>
			<MenuItem onClick={() => handleMobileMenuClose()}>
				<IconButton color="inherit">
					<Badge badgeContent={ring} color="secondary">
						<NotificationsIcon/>
					</Badge>
				</IconButton>
				<p>Notifications</p>
			</MenuItem>
			<MenuItem onClick={e => handleProfileMenuOpen(e)}>
				<IconButton color="inherit">
					<AccountCircle/>
				</IconButton>
				<p>Profile</p>
			</MenuItem>
		</Menu>
	);

	return (
		<div className={classes.root}>
			<AppBar position="static">
				<Toolbar>
					<Typography className={classes.title} variant="h6" color="inherit" noWrap>
						ToDowa4ka
					</Typography>
					<div className={classes.search}>
						<div className={classes.searchIcon}>
							<SearchIcon/>
						</div>
						<InputBase
							placeholder="Search…"
							classes={{
								root: classes.inputRoot,
								input: classes.inputInput,
							}}
							onChange={(e) => {
								searchTodo(todoList, e.target.value);
							}}
							onBlur={(e) => {
								e.target.value = '';
								searchTodo(todoList, e.target.value);
							}}
						/>
					</div>

					<div className={classes.grow}/>
					<div className={classes.sectionDesktop}>
						<IconButton color="inherit">
							<Badge badgeContent={todoList.length} color="secondary">
								<MailIcon/>
							</Badge>
						</IconButton>
						<IconButton
							aria-owns={isMenuOpen ? 'material-appbar' : undefined}
							aria-haspopup="true"
							onClick={e => handleProfileMenuOpen(e)}
							color="inherit"
						>
							<AccountCircle/>
						</IconButton>
					</div>
					<div className={classes.sectionMobile}>
						<IconButton aria-haspopup="true" onClick={e => handleMobileMenuOpen(e)} color="inherit">
							<MoreIcon/>
						</IconButton>
					</div>
				</Toolbar>
			</AppBar>
			{renderMenu}
			{renderMobileMenu}
		</div>
	);
};

PrimarySearchAppBar.propTypes = {
	classes: PropTypes.object.isRequired,
};

const mapStateToProps = (store) => ({
	todoList: store.todoItems.todoList,
});

const mapDispatchToProps = {
	searchTodo,
};

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(styles)(PrimarySearchAppBar));
