import React, {Component} from 'react';
import {PropTypes} from 'prop-types';
import {connect} from "react-redux";
import {deleteTodo} from '../action/deleteTodo';

import TodoListItem from './TodoListItem';
import '../style/TodoList.style.scss';

class TodoList extends Component {

    render() {

        const {todoItems, deleteTodo} = this.props;
        return (
            <ul className='TodoList'>
                {/*<TodoListItem  description='first description' id='123' title='first title' completed={false}/>*/}
                {todoItems.map(item => <TodoListItem key={item.id} {...item} removeTodo={deleteTodo}/>)}
            </ul>
        )
    }
}

TodoList.propTypes = {
    todoItems: PropTypes.shape({
        id: PropTypes.string.isRequired,
        title: PropTypes.string.isRequired,
        description: PropTypes.string.isRequired,
        completed: PropTypes.bool.isRequired
    }).isRequired,
    removeTodo: PropTypes.func.isRequired
};

const mapStateToProps = (store) => {
    return {
        todoItems: store.todoItems
    }
};

const mapDispatchToProps = {
    deleteTodo
};

export default connect(mapStateToProps, mapDispatchToProps)(TodoList);
